mapper.select:
SELECT admin_contest_idx , title , content , DATE_FORMAT(contest_s_date, '%Y-%m-%d %H:%i:%s') as contest_s_date , DATE_FORMAT(contest_e_date, '%Y-%m-%d %H:%i:%s') as contest_e_date , DATE_FORMAT(submit_s_date, '%Y-%m-%d %H:%i:%s') as submit_s_date , DATE_FORMAT(submit_e_date, '%Y-%m-%d %H:%i:%s') as submit_e_date , DATE_FORMAT(create_date, '%Y-%m-%d %H:%i:%s') as create_date , create_user
,case when contest_e_date < SYSDATE() then '공모종료' when contest_s_date > SYSDATE() then '공모전' when contest_e_date > SYSDATE() and SYSDATE() > contest_s_date then '공모진행중'
end as ing FROM daul.contest_admin WHERE admin_contest_idx = #{admin_contest_idx}

mapper.insert:
INSERT INTO contest_user_attach
(
user_contest_idx
, org_file_name
, save_file_name
, file_size
, create_user
, del_chk
, create_date
, attach_type
)
VALUES
(
#{idx}
, #{org_file_name}
, #{save_file_name}
, #{file_size}
, #{create_user}
, 'N'
, SYSDATE()
, #{attach_type}
)

mapper.select:
SELECT COUNT(*)
FROM contest_user
WHERE create_user = #{create_user}
AND admin_contest_idx = #{admin_contest_idx}
AND del_chk = 'N'

mapper.update:
UPDATE daul.daul_notice
SET view_count = view_count + 1
WHERE notice_idx = #{notice_idx}

mapper.select:
SELECT * FROM daul_notice WHERE del_chk = 'N' AND notice_type='contest'
AND notice_idx > #{notice_idx}
ORDER BY notice_idx LIMIT 0 , 1;

SELECT * FROM daul_notice WHERE del_chk = 'N' AND notice_type='contest'
AND notice_idx < #{notice_idx}
ORDER BY notice_idx desc LIMIT 0 , 1;
