mapper.update:
UPDATE daul.daul_notice
   SET view_count = view_count + 1
 WHERE notice_idx = #{notice_idx}

mapper.select:
SELECT * 
  FROM daul_notice
 WHERE del_chk = 'N'

   AND notice_idx > #{notice_idx}

 ORDER BY notice_idx
 LIMIT 0 , 1;

SELECT * 
  FROM daul_notice
 WHERE del_chk = 'N'

   AND notice_idx < #{notice_idx}

 ORDER BY notice_idx desc
 LIMIT 0 , 1;
