mapper.update:
UPDATE daul.daul_qna
   SET view_count = view_count + 1
 WHERE qna_idx = #{qna_idx}

mapper.select:
SELECT * FROM daul_qna WHERE del_chk = 'N'
AND qna_idx > #{qna_idx}
AND parent_qna_idx is null ORDER BY qna_idx LIMIT 0 , 1;

SELECT * FROM daul_qna WHERE del_chk = 'N'
AND qna_idx < #{qna_idx}
AND parent_qna_idx is null ORDER BY qna_idx desc LIMIT 0 , 1;

SELECT create_user
  FROM daul_qna
 WHERE qna_idx = #{qna_idx}

mapper.update:
UPDATE daul.daul_qna
   SET question = #{question}
      ,content = #{content}
      ,update_user = #{update_user}
      ,update_date = sysdate()
      ,lock_chk = #{lock_chk}
WHERE qna_idx = #{qna_idx}
