mapper.insert:
INSERT INTO daul.suggestion_main
     (
       suggestion_idx
     , title
     , content
     , create_user
     , create_date
     , del_chk
     )
VALUES 
	 (
	   #{suggestion_idx}
     , #{title}
     , #{content}
     , #{create_user}
     , SYSDATE()
     , 'N'
	 )

INSERT INTO suggestion_attach
     (
       suggestion_idx
     , org_file_name
     , save_file_name
     , file_size
     , create_user
     , del_chk
     , create_date
     , attach_type
     )
VALUES
     (
       #{idx}
     , #{org_file_name}
     , #{save_file_name}
     , #{file_size}
     , #{create_user}
     , 'N'
     , SYSDATE()
     , #{attach_type}
     )

mapper.select:
SELECT @rownum:=@rownum+1 AS num , sub.suggestion_idx , sub.title , sub.content , sub.like_count , sub.create_user , DATE_FORMAT(sub.create_date, '%Y-%m-%d %H:%i:%s') as create_date , sub.opinion_cnt , sub.like_per , sub.name , sub.auth_type FROM ( SELECT sm.* , CASE WHEN (like_count / 50 * 100) >= 100 THEN 100 ELSE (like_count / 50 * 100) END AS like_per FROM ( SELECT s.suggestion_idx , s.title , s.content , (SELECT COUNT(*) FROM daul.user_like where parent_idx = s.suggestion_idx) AS like_count , s.create_user , s.create_date , (SELECT count(*) FROM suggestion_opinion a WHERE a.suggestion_idx = s.suggestion_idx) AS opinion_cnt , b.name ,b.auth_type FROM suggestion_main s LEFT JOIN daul.user b ON s.create_user = b.user_id WHERE del_chk != 'Y'
) sm
ORDER BY sm.create_date
ORDER BY sm.like_count, sm.create_date
ORDER BY sm.opinion_cnt, sm.create_date
) sub , (SELECT @rownum:=0) TMP WHERE 1=1
AND sub.like_per >= 100
AND DATE_FORMAT(DATE_SUB(SYSDATE(), INTERVAL 1 MONTH), '%Y-%m-%d') > sub.create_date
ORDER BY num DESC LIMIT #{startIndex}, #{pageSize}

mapper.update:
UPDATE daul.suggestion_opinion
   SET suggestion_step = suggestion_step + 1
 WHERE suggestion_ref = #{suggestion_ref}

   AND suggestion_step > #{suggestion_step}

mapper.insert:
		INSERT INTO daul.suggestion_opinion
		     (
		       opinion_idx
             , suggestion_idx
             , opinion_content
             , like_count
             , create_user
             , create_date
             , del_chk
             , suggestion_ref
             , suggestion_indent
             , suggestion_step
             , emotion_type
             , emotion_score
		     )
		VALUES
		     (
		       #{opinion_idx}
             , #{suggestion_idx}
             , #{opinion_content}
             , #{like_count}
             , #{create_user}
             , SYSDATE()
             , 'N'
             , #{suggestion_ref}
             , #{suggestion_indent}
             , #{suggestion_step}
             , #{emotion_type}
             , #{emotion_score}
		     )

mapper.update:
UPDATE daul.suggestion_opinion
   SET del_chk = 'Y'
 WHERE opinion_idx = #{opinion_idx}

mapper.select:
SELECT *
  FROM daul.user_like
 WHERE parent_idx = #{suggestion_idx}
   AND user_id = #{user_id}

mapper.insert:
INSERT INTO daul.user_like
	 (
	   user_id
	 , parent_idx
	 )
VALUES
	 (
	   #{user_id}
	 , #{suggestion_idx}
	 )

mapper.select:
SELECT COUNT(*)
  FROM daul.user_like
 WHERE parent_idx = #{suggestion_idx}

mapper.delete:
DELETE FROM daul.user_like
 WHERE parent_idx = #{suggestion_idx}
   AND user_id = #{user_id}

mapper.select:
SELECT *
  FROM daul.suggestion_attach
 WHERE del_chk != 'Y'
   AND suggestion_idx = #{suggestion_idx}

   AND attach_type = #{attach_type}

SELECT COUNT(*)
  FROM daul.suggestion_opinion
 WHERE suggestion_idx = #{suggestion_idx}

mapper.update:
UPDATE daul.suggestion_attach
   SET del_chk = 'Y'
 WHERE save_file_name = #{save_file_name}
   AND del_chk != 'Y'

   AND attach_type = #{attach_type}

UPDATE daul.suggestion_main
   SET update_user = #{update_user}
     , update_date = SYSDATE()

     , title = #{title}


     , content = #{content}

 WHERE suggestion_idx = #{suggestion_idx}

UPDATE daul.suggestion_main
   SET del_chk = 'Y'
     , update_user = #{update_user}
     , update_date = SYSDATE()
 WHERE suggestion_idx = #{suggestion_idx}

UPDATE daul.suggestion_opinion
   SET del_chk = 'Y'
     , update_user = #{update_user}
     , update_date = SYSDATE()
 WHERE suggestion_idx = #{suggestion_idx}
   AND del_chk != 'Y'

mapper.insert:
