mapper.insert:
INSERT INTO daul.user
	 (
	   user_id
	 , name
	 , pw
	 , email
	 , phone
	 , channel
	 , reg_date
	 , last_login_date

	 , email_chk 


	 , talk_chk 


	 , sms_chk 

	 , auth_type
	 , kakao_key
	 )
VALUES 
	 (
	   #{user_id}
	 , #{name}
	 , #{pw}
	 , #{email}
	 , #{phone}
	 , #{channel}
	 , SYSDATE()
	 , SYSDATE()

	 , #{email_chk} 


	 , #{talk_chk} 


	 , #{sms_chk} 

	 , #{auth_type}
	 , #{kakao_key}
	 )

mapper.select:
SELECT COUNT(*)
  FROM daul.user
 WHERE user_id = #{user_id}

