mapper.update:
UPDATE daul.user
   SET user_id = #{user_id}

     , pw = #{pw}


     , name = #{name}


     , email = #{email}


     , email_chk = #{email_chk}


     , talk_chk = #{talk_chk}


     , sms_chk = #{sms_chk}

 WHERE user_id = #{user_id}
